import Foundation

class Problem8 {
    
    init() {
        print("Problem 8: \(problem8())")
    }
    
    func problem8() -> Int {
        
        let numStr = ""
        
        let strArr = Array(numStr.characters)
        
        var numArr = [Int]()
        
        for num in strArr {
            numArr.append(Int(String(num))!)
        }
        
        var maxprodukt = 0
        var produkt:Int
        for i in 0 ..< numArr.count-12 {
            produkt = numArr[i]
            for j in 1 ..< 13 {
                produkt *= numArr[i+j]
            }
            
            if produkt > maxprodukt {
                maxprodukt = produkt
            }
        }
        
        return maxprodukt
    }
}